$(function () {

    $('.featured_products__wrapper').slick({
        dots: false,
        arrows: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        prevArrow: '<button class="slick-arrow slick-prev"> <img src="/images/featured_products/angle-left.svg" alt=""></button>',
        nextArrow: '<button class="slick-arrow slick-next"> <img src="/images/featured_products/angle-right.svg" alt=""></button>',
    });

    $(".rate-star").rateYo({
        rating: 4.5,
        starWidth: "15px",
        readOnly: true,
        ratedFill: "#ffc000",
        normalFill: "rgb(204, 204, 204)"
    });



    $('.your-followers__items__inner').slick({
        dots: false,
        arrows: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        prevArrow: '<button class="slick-arrow slick-prev"> <img src="/images/featured_products/angle-left.svg" alt=""></button>',
        nextArrow: '<button class="slick-arrow slick-next"> <img src="/images/featured_products/angle-right.svg" alt=""></button>',
    });

    $(".js-range-slider").ionRangeSlider({
        type: "double",
        grid: true,
        min: 0,
        max: 350,
        from: 30,
        to: 300,
        prefix: "$"

    });

    $('.icon-th-list').on('click', function () {
        $('.new-products__item').addClass('list');
        $('.icon-th-list').addClass('active');
        $('.icon-th-large').removeClass('active');
    });

    $('.icon-th-large').on('click', function () {
        $('.new-products__item').removeClass('list');
        $('.icon-th-large').addClass('active');
        $('.icon-th-list').removeClass('active');
    });


    var mixer = mixitup('.new-products__inner-box');
});